#!/usr/bin/python
# -*- coding: UTF-8 -*-

import os
import base64
import pygame


class Image():
    """获取图片的类-图片以二进制形式获取"""

    def __init__(self):
        """初始化获取图片的类"""
        self.alienstr = ''
        self.shipstr = ''

    def getImage(self, type):
        if type == 'alien':
            str = self.alienstr
        elif type == 'ship':
            str = self.shipstr

        # 临时文件用来保存jpg文件
        tmp = open('tmp.bmp', 'wb+')
        tmp.write(base64.b64decode(str))
        tmp.close()

        image = pygame.image.load('tmp.bmp')
        os.remove('tmp.bmp')

        return image
